 SELECT a.CountryRegion AS Continent, SUM(sod.LineTotal) AS TotalEarnings FROM SalesLT.SalesOrderHeader soh JOIN SalesLT.SalesOrderDetail sod ON soh.SalesOrderID = sod.SalesOrderID JOIN SalesLT.Customer c ON soh.CustomerID = c.CustomerID JOIN SalesLT.CustomerAddress ca ON c.CustomerID = ca.CustomerID JOIN SalesLT.Address a ON ca.AddressID = a.AddressID WHERE DATEPART(QUARTER, soh.OrderDate) = 1 GROUP BY a.CountryRegion ORDER BY TotalEarnings DESC;